import React from 'react';
import { makeStyles } from '@material-ui/core/styles';
import SpeedDial from '@material-ui/lab/SpeedDial';
import SpeedDialIcon from '@material-ui/lab/SpeedDialIcon';
import SpeedDialAction from '@material-ui/lab/SpeedDialAction';
import Fab from '@material-ui/core/Fab';
import ArrowLeftIcon from '@material-ui/icons/ArrowLeft';
import Typography from '@material-ui/core/Typography';
import List from '@material-ui/core/List';
import ListItem from '@material-ui/core/ListItem';
import ListItemIcon from '@material-ui/core/ListItemIcon';
import ListItemText from '@material-ui/core/ListItemText';
import Link from '@material-ui/core/Link';
import Breadcrumbs from '@material-ui/core/Breadcrumbs'

const useStyles = makeStyles((theme) => ({
    speedDial: {
        position: 'absolute',
        bottom: theme.spacing(2),
        right: theme.spacing(2),
    },
    button: {
        '&:hover': {
            background: "#EA2027", color: "white"
        },
        background: "#EA2027", color: "white"
    }
}));

const actions = [
    { icon: <SpeedDialIcon />, name: 'Donations', href: '/donations' },
    { icon: <SpeedDialIcon />, name: 'Trails', href: '/trails' },
    { icon: <SpeedDialIcon />, name: 'Status', href: '/status' },
];

const status =
{
    positive: { name: 'Confirmed', color: 'red' },
    sick: { name: 'Sick', color: 'orange' },
    negative: { name: 'Sick', color: 'orange' },
    recovered: { name: 'Recovered', color: 'green' },
    "not sick": { name: 'Not Sick', color: 'gray' }
}

const isSick = localStorage.isSick;

export default function Dashboard(props) {
    const classes = useStyles();
    const [open, setOpen] = React.useState(false);
    const [hidden, setHidden] = React.useState(false);

    const handleOpen = () => {
        setOpen(true);
    };

    const handleClose = () => {
        setOpen(false);
    };

    function handleClick(event) {
        event.preventDefault();
        console.info('You clicked a breadcrumb.');
    }

    const [error, setError] = React.useState(null);
    const [isLoaded, setIsLoaded] = React.useState(false);
    const [data, setData] = React.useState({ confirmed: null, deaths: null, recovered: null });

    React.useEffect(() => {
        fetch("https://covid19api.herokuapp.com/latest")
            .then(res => res.json())
            .then(
                (result) => {
                    setIsLoaded(true);
                    setData(result);
                },
                (error) => {
                    setIsLoaded(true);
                    setError(error);
                }
            )
    }, [])

    let donate_link = null;
    let trial_link = null;
    if (localStorage.isSick == "not sick" && localStorage.tested == "positive") {
        donate_link = <Link href="#" onClick={preventDefault} style={{ color: '#F2C94C' }}>Join a clinical trial</Link>
    }
    else {
        donate_link = null;
    }

    const preventDefault = (event) => event.preventDefault();

    return (
        <div className="Dashboard">
            <Breadcrumbs aria-label="breadcrumb" className="breadcrumbs">
                <Link color="inherit">myTrials</Link>
                <Link color="inherit">myDonations</Link>
                <Link color="inherit" href="/questions">myRecords</Link>
            </Breadcrumbs>
            <div className="row status-wrapper">
                <div className="col">
                    <h3>MY STATUS</h3>
                    <div className="row status-list">
                        <span className="dot" style={{ background: status[isSick].color }}></span>
                        {status[isSick].name}

                    </div>
                    {/* {isSick?sickButtons:null} */}
                </div>
                <div className="col update-list" style={{ textAlign: 'right' }}>
                    <h3>LATEST UPDATE</h3>
                    <div>
                        <div>Total Cases: {data.confirmed}</div>
                        <div>Total Deaths: {data.deaths}</div>
                        <div>Total Recovered: {data.recovered}</div>
                    </div>
                </div>



            </div>

            <div className="col suggestions-wrapper">
                <h3>SUGGESTIONS</h3>
                <Link href="#" onClick={preventDefault} style={{ color: '#FFFFFF' }}>Stay at home</Link>
                <Link href="#" onClick={preventDefault} style={{ color: '#2D9CDB' }}>Download HomeBound</Link>
                {/* <a style={{display: "table-cell"}} href="someLink" target="_blank">text</a> */}
                {trial_link}
                {donate_link}
            </div>
            <div className={classes.root}>
                <SpeedDial
                    ariaLabel="SpeedDial tooltip example"
                    className={classes.speedDial}
                    hidden={hidden}
                    icon={<SpeedDialIcon />}
                    onClose={handleClose}
                    onOpen={handleOpen}
                    open={open}
                    FabProps={{ className: classes.button }}
                >
                    {actions.map((action) => (
                        <SpeedDialAction
                            key={action.name}
                            icon={action.icon}
                            display={{ background: action.color }}
                            tooltipTitle={action.name}
                            tooltipOpen
                            onClick={() => props.history.push(action.href)}
                        />
                    ))}
                </SpeedDial>

            </div>
            {/* <Fab style={{ background: "#9206FF" }} aria-label="add" href="/symptoms" size="medium" className="back-btn">
                <ArrowLeftIcon />
            </Fab> */}
        </div>
    );
}
